import React, {Component} from 'react';



class Excursion extends Component {
    // static defaultProps = {
    //     country: 'Antigua',
    //     destination: 'Antigua'
    // }
    constructor(props){
        super(props);
        this.state = {
            excursion: ""
        }
    }


    componentDidMount(){
        if(this.props.destination === undefined){
            fetch(`https://hotelinfoservice.sunwingtravelgroup.com/1/en/excursionsCountryDestination/${this.props.country}/${this.props.eDestination}`)
                .then(res => res.json())
                .then(data => {
                    this.setState({
                        excursion: data,
                        country: this.props.country,
                        destination: this.props.eDestination
                    });
                });
        }else {
            fetch(`https://hotelinfoservice.sunwingtravelgroup.com/1/en/excursionsCountryDestination/${this.props.destination.country}/${this.props.destination.destination}`)
                .then(res => res.json())
                .then(data => {
                    this.setState({
                        excursion: data,
                        country: this.props.destination.country,
                        destination: this.props.destination.destination
                    });
                });
        }

    }

    render(){

        const categories = () => {
            // const category = Object.values(this.state.excursion);

            // for(const obj of category){
            //     // console.log(obj);
            //     const categorySection = document.getElementById('categories');
            //     const div = document.createElement('div');
            //     div.innerHTML = "<h3>" + obj['categoryName'] + "</h3>"+"<div id='sub'></div>";
            //     categorySection.appendChild(div);
            //
            //     // const sub = Object.values(obj);
            //     // for (const subCat of sub){
            //     //     console.log(subCat[0]['subCategoryName']);
            //     //     if(subCat[0]['subCategoryName'] !== undefined){
            //     //         const subCategorySection = document.getElementById('sub');
            //     //         const subDiv = document.createElement('div');
            //     //         subDiv.innerHTML = "<h4>" + subCat[0]['subCategoryName'] + "</h4>";
            //     //         subCategorySection.appendChild(subDiv);
            //     //     }
            //     // }
            // }

        }
        categories();

        return(
            <div>

                <section className={'hero'}>
                    <div>
                        <h1>{this.state.country}</h1>
                        <p>{this.state.destination}</p>
                    </div>
                </section>
                <section>
                    <div id={'categories'}>
                        {
                            Object.keys(this.state.excursion).map((obj, i)=>{
                                const title = this.state.excursion[obj]['categoryName'];
                                Object.values(obj).map((sub, i)=>{
                                    console.log(sub)
                                })
                                return(
                                    <h3 key={i}>{title}</h3>
                                );


                            })
                        }
                    </div>
                </section>
                {/*<pre>{JSON.stringify(this.state.excursion, null, 2)}</pre>*/}
            </div>


        );
    }
}

Excursion.defaultProps = {
    country: 'Antigua',
    eDestination: 'Antigua'
}

export default Excursion;
