class FetchInfo extends Component {

    constructor(props){
        super(props);
        this.state={
            response:[]
        }
    }

    componentDidMount(){
        fetch('https://hotelinfoservice.sunwingtravelgroup.com/en/AllHotelDestinationList')
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    response: data
                });
            });
    }


    render(){



        const selectCountry = (options) => {
            return (
                <select id={'countryName'} onChange={destinationSelect}>
                    <option>--Select Country---</option>
                {this.state.response.map((country, option) => (
                    <option key={country[options]}>{country[options]}</option>))}
                </select>

            )

        }

        const renderDestination = (values) => {
            this.state.response.map((country, index) => {
                if(country.countryName == values){
                    console.log(country.destinations);
                    const select = document.getElementById('destination');
                    const createOption = document.createElement('option');
                    createOption.test = 'test';
                    select.add(createOption);
                }
            });
        }

        const renderTest = () => {
            return <select><option>Test</option></select>
        }

        const destinationSelect = () => {



            // const values = countrySelect.options[countrySelect.selectedIndex].text;

                setTimeout(function(){
                    const countrySelect = document.getElementById('countryName');
                    const values = countrySelect.options[countrySelect.selectedIndex].text;
                    return (renderDestination(values));
                },100)





        }


        return(
            <div>

                {/*<pre>*/}
                    {/*{JSON.stringify(this.state.response, null, 2)}*/}
                {/*</pre>*/}


                {selectCountry('countryName')}
                <select id={'destination'}></select>


            </div>

        );
    }
}

export default FetchInfo;
