import React, {Component} from 'react';

class FetchInfo extends Component {

    constructor(props){
        super(props);
        this.state={
            response:[]
        }
    }

    componentDidMount(){
        fetch('https://hotelinfoservice.sunwingtravelgroup.com/en/AllHotelDestinationList')
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    response: data
                });
            });
    }


    render(){

        const checkSelect = id => {
            const e = document.getElementById(id);
            const values = e.options[e.selectedIndex].text;
            return values;
        }
        const options = setTimeout(function(){
            console.log(checkSelect('selectCountry'))

        }, 1000);

        const selectCountry = (option) => (
            this.state.response.map((country, option)=>(<option key={country[option]}>{country[option]}</option>))
        )

        return(
            <div>

                <pre>
                    {JSON.stringify(this.state.response, null, 2)}
                </pre>

                <select id={'selectCountry'}>
                    {selectCountry('countryName')}
                </select>

            </div>

        );
    }
}

export default FetchInfo;
