import Footer from './components/footer';

import './styles/styles.css';

class App extends Component {
    constructor(props){
        super(props);
        this.state={
            response:[],
            hide: false
        }
        this.addObj = this.addObj.bind(this);
    }

    componentDidMount(){
        fetch('https://hotelinfoservice.sunwingtravelgroup.com/en/AllHotelDestinationList')
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    response: data
