import React, {Component} from 'react';
import {HashRouter, NavLink} from "react-router-dom";

class Excursion extends Component {
    constructor(props){
        super(props);
        this.state = {
            excursion: ""
        }
    }


    componentDidMount(){
        if(this.props.destination == undefined){
            fetch(`https://hotelinfoservice.sunwingtravelgroup.com/1/en/excursionsCountryDestination/Antigua/Antigua`)
                .then(res => res.json())
                .then(data => {
                    this.setState({
                        excursion: data
                    });
                });
        }else {
            fetch(`https://hotelinfoservice.sunwingtravelgroup.com/1/en/excursionsCountryDestination/${this.props.destination.country}/${this.props.destination.destination}`)
                .then(res => res.json())
                .then(data => {
                    this.setState({
                        excursion: data
                    });
                });
        }

    }

    render(){

        const selectCountry = (options) => {
            return (
                <select id={'countryName'} onChange={destinationSelect}>
                    <option>--SELECT COUNTRY--</option>
                    {this.props.data.map((country, option) => (
                        <option key={country[options]}>{country[options]}</option>))}
                </select>

            )

        }

        const renderDestination = (values) => {
            this.props.data.filter(function(country){
                if(country.countryName === values){
                    // console.log(country.destinations);

                    document.getElementById('destination').innerHTML = "";
                    country.destinations.filter((destination, index) => {
                        const select = document.getElementById('destination');
                        const createOption = document.createElement('option');
                        createOption.text = destination;
                        select.add(createOption);
                        return true;
                    });

                }
                return true;
            });
        }

        const destinationSelect = () => {
            setTimeout(function(){
                const countrySelect = document.getElementById('countryName');
                const values = countrySelect.options[countrySelect.selectedIndex].text;
                return (renderDestination(values));
            },100)

        }

        return(
            <div>
                <section>
                    <div className={'excursionBox'}>

                        <h1>Excursion Destination</h1>

                        <div className={'excursionSelect'}>

                            {selectCountry('countryName')}

                            <select id={'destination'}>

                            </select>

                        </div>
                        <HashRouter>
                            <NavLink to="/excursion">GO</NavLink>
                        </HashRouter>

                    </div>
                </section>
                <pre>{JSON.stringify(this.state.excursion, null, 2)}</pre>
            </div>


        );
    }
}

export default Excursion;
