import React, { Component } from 'react';
import Header from './components/header';
import FetchInfo from './components/ajax';
import {Route, HashRouter} from 'react-router-dom';
// import Excursion from './components/excursion';
import Footer from './components/footer';

import './styles/styles.css';
import Excursion from "./components/excursion";

class App extends Component {
    constructor(props){
        super(props);
        this.state={
            response:[],
            hide: false
        };
        this.hideMe = this.hideMe.bind(this);

        this.addObj = this.addObj.bind(this);
        this.excursionObj= {};
        this.ifExist = setInterval(()=>{

            const countrySelect = document.getElementById('countryName');
            if(countrySelect.children.length > 0 && document.getElementById('destination').children.length > 0){
                let countryValue = countrySelect.options[countrySelect.selectedIndex].text;
                this.excursionObj.country = countryValue;
                const destinationSelect = document.getElementById('destination');
                if(destinationSelect.children.length <1){
                    this.excursionObj.destination = "";
                }

                const optionsArray = [countrySelect, destinationSelect];
                let destinationValue = destinationSelect.options[destinationSelect.selectedIndex].text;

                    optionsArray.forEach(function(option){
                        option.addEventListener("change", function () {

                            setTimeout(function(){
                                countryValue = countrySelect.options[countrySelect.selectedIndex].text;
                                if(destinationSelect.children.length <1){
                                    destinationValue = "";
                                } else {
                                    destinationValue = destinationSelect.options[destinationSelect.selectedIndex].text;
                                }

                                console.log(destinationValue);
                                console.log(countryValue);


                            }, 200);

                        });
                    });

                this.excursionObj.ccountry = countryValue;
                this.excursionObj.destination = destinationValue;
                clearInterval(this.ifExist);
                return(this.addObj(this.excursionObj));

            }
        }, 100);
    }

    componentDidMount(){
        fetch('https://hotelinfoservice.sunwingtravelgroup.com/en/AllHotelDestinationList')
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    response: data
                });
            });
    }

    addObj = (value) => {
        this.setState({
            country: value.country,
            destination: value.destination
        });
    };

    hideMe(){
        this.setState({hide: !this.state.hide})
    }

    render() {
console.log(this.state);
    if(this.state.hide === false){
        return (
            <div className="App">
                <HashRouter>
                    <div className={'appContent'}>
                        <Header />
                        <div>
                            <FetchInfo data={this.state.response} country={this.state.country} destination={this.state.destination} hide={this.hideMe}/>
                        </div>
                        <Footer/>
                    </div>
                </HashRouter>
            </div>
        );
    }else {
        return (
            <div className="App">
                <HashRouter>
                    <div className={'appContent'}>
                        <Header />
                        <div>
                            <Route path="/excursion" render={(props)=><Excursion {...props} country={this.state.country} destination={this.state.destination}/>} />
                        </div>
                        <Footer/>
                    </div>
                </HashRouter>
            </div>
        );
    }
  }
}



export default App;
