import React, {Component} from 'react';



class Excursion extends Component {
    // static defaultProps = {
    //     country: 'Antigua',
    //     destination: 'Antigua'
    // }
    constructor(props){
        super(props);
        this.state = {
            excursion: ""
        }
    }


    componentDidMount(){
        if(this.props.destination === undefined){
            fetch(`https://hotelinfoservice.sunwingtravelgroup.com/1/en/excursionsCountryDestination/Antigua/Antigua`)
                .then(res => res.json())
                .then(data => {
                    this.setState({
                        excursion: data
                    });
                });
        }else {
            fetch(`https://hotelinfoservice.sunwingtravelgroup.com/1/en/excursionsCountryDestination/${this.props.destination.country}/${this.props.destination.destination}`)
                .then(res => res.json())
                .then(data => {
                    this.setState({
                        excursion: data
                    });
                });
        }

    }

    render(){

        const categories = () => {


            const category = Object.values(this.state.excursion);
            // const checkElement = setInterval(function(){
            //     if(document.getElementById('categories') !== undefined){
            //         document.getElementById('categories').innerHTML = "";
            //     }
            // }, 100);
            // console.log(category);
            for(const obj of category){
                console.log(obj);
                const categorySection = document.getElementById('categories');
                const div = document.createElement('div');
                div.innerHTML = "<p>" + obj['categoryName'] + "</p>";
                categorySection.appendChild(div);
            }
        }
        categories();

        return(
            <div>
                {/*<ExcursionList data={this.props.data}/>*/}
                <section className={'hero'}>
                    <h1>{this.props.destination.country}</h1>
                    <p>{this.props.destination.destination}</p>
                </section>
                <section>
                    <div id={'categories'}>
                        {console.log(this.props.destination)}
                    </div>
                </section>
                {/*<pre>{JSON.stringify(this.state.excursion, null, 2)}</pre>*/}
            </div>


        );
    }
}

Excursion.defaultProps = {
    country: 'Antigua',
    eDestination: 'Antigua'
}

export default Excursion;
