import React, { Component } from 'react';
import Header from './components/header';
import FetchInfo from './components/ajax';
import {Route, HashRouter} from 'react-router-dom';
import Excursion from './components/excursion';

import './styles/styles.css';

class App extends Component {
    constructor(props){
        super(props);
        this.state={
            response:[]
        }
    }

    componentDidMount(){
        fetch('https://hotelinfoservice.sunwingtravelgroup.com/en/AllHotelDestinationList')
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    response: data
                });
            });
    }
  render() {
    var excursionObj;
    const checkExist = setInterval(function(){
        const countrySelect = document.getElementById('countryName');

        if(countrySelect !== null){
            clearInterval(checkExist);

            const destinationSelect = document.getElementById('destination');

            const optionsArray = [countrySelect, destinationSelect];

            const countryValue = countrySelect.options[countrySelect.selectedIndex].text;
            const destinationValue = destinationSelect.options[destinationSelect.selectedIndex].text;
            excursionObj = {
                country: countryValue,
                destination: destinationValue || ""
            };

            optionsArray.forEach(function(option){
                option.addEventListener("change", function () {

                    setTimeout(function(){
                        const countryValue = countrySelect.options[countrySelect.selectedIndex].text;
                        const destinationValue = destinationSelect.options[destinationSelect.selectedIndex].text;
                        excursionObj = {
                            country: countryValue,
                            destination: destinationValue || ""
                        };

                    }, 200);

                });
            });

            return excursionObj;
        }
    }, 100);
    return (
      <div className="App">
        <HashRouter>
            <div>
            <Header />
            <div>
                <Route exact path="/" render={(props)=><FetchInfo {...props} data={this.state.response} />} />
                <Route path="/excursion" render={(props)=><Excursion {...props} destination={excursionObj} />} />
            </div>
            </div>
        </HashRouter>
      </div>
    );
  }
}



export default App;
